import * as ease from 'ease'
import {MEASURE} from './Proton'

/** @private */
var BEHAVIOUR_ID = 0

/**
 * The Behaviour class is the base for the other Behaviour
 */
export default class Behaviour {
	/**
	 * @param {Number} life the life time
	 * @param {String} easing the easing function
	 */
	constructor(life = Infinity, easing) {
		/**
		 * The behaviour's id
		 * @property {String} id
		 */
		this.id = 'Behaviour_' + BEHAVIOUR_ID++

		/**
		 * The behaviour's life
		 * @property {Number} life
		 */
		this.life = life

		/**
		 * The behaviour's decaying trend, for example Proton.easeOutQuart
		 * @property {Function} easing
		 * @default Proton.easeLinear
		 */
		this.easing = easing || ease.Linear

		/** @private TODO doc */
		this.age = 0
		/** @private TODO doc */
		this.energy = 1

		/**
		 * The behaviour is Dead
		 * @property {Boolean} dead
		 */
		this.dead = false

		/**
		 * The behaviour's parents array
		 * @property {Array} parents
		 */
		this.parents = []

		// speed hack
		//this._applyBehaviour = this.applyBehaviour
		this._applyBehaviour = Behaviour.prototype.applyBehaviour
	}


	/**
	 * Reset this behaviour's parameters
	 * @param {Number} this behaviour's life
	 * @param {String} this behaviour's easing
	 */
	reset(life = Infinity, easing) {
		this.life = life
		this.easing = easing || ease.Linear
	}

	/**
	 * Normalize a force by 1:100
	 * @param {Vector2D} force
	 * @return {Vector2D}
	 */
	normalizeForce(force) {
		return force.multiplyScalar(MEASURE)
	}

	/**
	 * Normalize a value by 1:100
	 * @param {Number} value
	 * @return {Number}
	 */
	normalizeValue(value) {
		return value * MEASURE
	}

	/**
	 * Initialize the behaviour's parameters for all particles
	 * @param {Particle} particle
	 * @abstract
	 */
	initialize(particle) {}

	/**
	 * Apply this behaviour for all particles every time
	 * @param {Particle} particle
	 * @param {Number} time the integrate time 1/ms
	 * @param {Number} index the particle index
	 */
	applyBehaviour(particle, time, index) {
		this.age += time
		if (this.age >= this.life || this.dead) {
			this.energy = 0
			this.dead = true
			this.destory()
		} else {
			var scale = this.easing(particle.age, particle.life)
			this.energy = Math.max(1 - scale, 0)
		}
	}

	/**
	 * Destory this behaviour
	 */
	destory() {
		for (var i = 0, l = this.parents.length; i < l; i++) {
			this.parents[i].removeBehaviour(this)
		}
		this.parents = []
	}
}

import Emitter from './Emitter'

/**
 * The BehaviourEmitter class inherits from Proton.Emitter
 *
 * use the BehaviourEmitter you can add behaviours to self
 */
export default class BehaviourEmitter extends Emitter {
	/**
	 * @param {Object} pObj the parameters object
	 */
	constructor(pObj) {
		super(pObj)
		this.selfBehaviours = []
	}

	/**
	 * add the Behaviour to emitter
	 *
	 * you can use Behaviours array:emitter.addSelfBehaviour(Behaviour1,Behaviour2,Behaviour3)
	 * @param {Behaviour} behaviour like this new Proton.Color('random')
	 */
	addSelfBehaviour() {
		for (var i = 0, l = arguments.length; i < l; i++) {
			this.selfBehaviours.push(arguments[i])
		}
	}

	/**
	 * remove the Behaviour for self
	 * @param {Behaviour} behaviour a behaviour
	 */
	removeSelfBehaviour(behaviour) {
		var index = this.selfBehaviours.indexOf(behaviour)
		if (index > -1)
			this.selfBehaviours.splice(index, 1)
	}

	/** @override */
	update(time) {
		super.update(time)
		if (!this.sleep) {
			var selfBehaviours = this.selfBehaviours
			for (var i = 0, l = selfBehaviours.length; i < l; i++) {
				selfBehaviours[i].applyBehaviour(this, time, i)
			}
		}
	}
}

import EventEmitter from 'eventemitter3'

import {
	integrator, pool,
	PARTICLE_CREATED, PARTICLE_UPDATE, PARTICLE_DEAD
} from './Proton'
import Particle from './Particle'
import {Initialize, Rate} from './Initialize'
import {degreeTransform} from './util'


//setPrototypeByObject
//setVector2DByObject

/** @private */
var EMITTER_ID = 0

/**
 * You can use this emit particles.
 *
 * It will dispatch follow events:
 * PARTICLE_CREATED
 * PARTICLE_UPDATA
 * PARTICLE_DEAD
 *
 * @extends {Particle}
 * @extends {EventEmitter}
 */
export default class Emitter extends Particle {
	 /**
	 * @param {Object} pObj the parameters object
	 * for example {damping:0.01,bindEmitter:false}
	 */
	constructor(pObj) {
		super(pObj)

		// TODO EventEmitter
		this._events = undefined
		this.addEventListener = EventEmitter.prototype.on
		this.removeEventListener = EventEmitter.prototype.off
		this.removeAllEventListeners = EventEmitter.prototype.removeAllListeners
		//this.hasEventListener = EventEmitter.hasEventListener
		this.dispatchEvent = EventEmitter.prototype.emit

		this.initializes = []
		this.particles = []
		this.behaviours = []
		this.emitTime = 0
		this.emitTotalTimes = -1

		/**
		 * The friction coefficient for all particle emit by This
		 * @property {Number} damping
		 * @default 0.006
		 */
		this.damping = .006

		/**
		 * If bindEmitter the particles can bind this emitter's property
		 * @property {Boolean} bindEmitter
		 * @default true
		 */
		this.bindEmitter = true

		/**
		 * The number of particles per second emit (a [particle]/b [s])
		 * @property {Rate} rate
		 * @default Rate(1, .1)
		 */
		this.rate = new Rate(1, .1)

		/**
		 * The emitter's id
		 * @property id
		 * @type {String} id
		 */
		this.id = 'emitter_' + EMITTER_ID++
	}

	/**
	 * start emit particle
	 * @param {Number} emitTime begin emit time
	 * @param {String} life the life of this emitter
	 */
	emit(emitTime = Infinity, life) {
		this.emitTime = 0
		this.emitTotalTimes = emitTime

		if (life == true || life == 'life' || life == 'destroy') {
			if (emitTime == 'once')
				this.life = 1
			else
				this.life = this.emitTotalTimes

		} else if (!isNaN(life)) {
			this.life = life
		}

		this.rate.init()
	}

	/**
	 * stop emiting
	 */
	stopEmit() {
		this.emitTotalTimes = -1
		this.emitTime = 0
	}

	/**
	 * remove current all particles
	 */
	removeAllParticles() {
		var particles = this.particles
		for (var i = 0, l = particles.length; i < l; i++) {
			particles[i].dead = true
		}
	}
	/**
	 * create single particle
	 * 
	 * can use emit({x:10},new Gravity(10),{'particleUpdate',fun}) or emit([{x:10},new Initialize],new Gravity(10),{'particleUpdate',fun})
	 */
	createParticle(initialize, behaviour) {
		var particle = pool.get()
		this.setupParticle(particle, initialize, behaviour)
		this.dispatchEvent(PARTICLE_CREATED, particle)
		return particle
	}
	/**
	 * add initialize to this emitter
	 */
	addSelfInitialize(pObj) {
		if (pObj['init']) {
			pObj.init(this)
		} else {
			this.initAll()
		}
	}
	/**
	 * add the Initialize to particles
	 * 
	 * you can use initializes array:for example emitter.addInitialize(initialize1,initialize2,initialize3)
	 * @param {Initialize} initialize like this new Radius(1, 12)
	 */
	addInitialize() {
		for (var i = 0, l = arguments.length; i < l; i++) {
			this.initializes.push(arguments[i])
		}
	}
	/**
	 * remove the Initialize
	 * @param {Initialize} initialize a initialize
	 */
	removeInitialize(initializer) {
		var index = this.initializes.indexOf(initializer)
		if (index > -1) {
			this.initializes.splice(index, 1)
		}
	}

	/**
	 * remove all Initializes
	 */
	removeInitializers() {
		this.initializes.length = 0
	}
	/**
	 * add the Behaviour to particles
	 * 
	 * you can use Behaviours array:emitter.addBehaviour(Behaviour1,Behaviour2,Behaviour3)
	 * @param {Behaviour} behaviour like this new Color('random')
	 */
	addBehaviour() {
		for (var i = 0, l = arguments.length; i < l; i++) {
			this.behaviours.push(arguments[i])
			if (arguments[i].hasOwnProperty("parents"))
				arguments[i].parents.push(this)
		}
	}
	/**
	 * remove the Behaviour
	 * @param {Behaviour} behaviour a behaviour
	 */
	removeBehaviour(behaviour) {
		var index = this.behaviours.indexOf(behaviour)
		if (index > -1)
			this.behaviours.splice(index, 1)
	}
	/**
	 * remove all behaviours
	 */
	removeAllBehaviours() {
		this.behaviours.length = 0
	}

	integrate(time) {
		var damping = 1 - this.damping
		integrator.integrate(this, time, damping)
		var particles = this.particles
		for (var i = 0, l = particles.length; i < l; i++) {
			var particle = particles[i]
			particle.update(time, i)
			integrator.integrate(particle, time, damping)

			this.dispatchEvent(PARTICLE_UPDATE, particle)
		}
	}

	emitting(time) {
		if (this.emitTotalTimes == 'once') {
			var count = this.rate.getValue(99999)
			for (var i = count - 1; i >= 0; i--) {
				this.createParticle()
			}

			this.emitTotalTimes = 'none'
		} else if (!isNaN(this.emitTotalTimes)) {
			this.emitTime += time
			if (this.emitTime < this.emitTotalTimes) {
				var count = this.rate.getValue(time)
				for (var i = count - 1; i >= 0; i--) {
					this.createParticle()
				}
			}
		}
	}

	update(time) {
		this.age += time
		if ((this.age >= this.life) || this.dead) {
			this.destroy()
			return
		}

		this.emitting(time)
		this.integrate(time)

		var particles = this.particles
		for (var k = particles.length - 1; k >= 0; k--) {
			var particle = this.particles[k]
			if (particle.dead) {
				pool.set(particle)
				particles.splice(k, 1)
				this.dispatchEvent(PARTICLE_DEAD, particle)
			}
		}
	}

	setupParticle(particle, initialize, behaviour) {
		var initializes = this.initializes
		var behaviours = this.behaviours

		if (initialize) {
			if ( initialize instanceof Array)
				initializes = initialize
			else
				initializes = [initialize]
		}

		if (behaviour) {
			if ( behaviour instanceof Array)
				behaviours = behaviour
			else
				behaviours = [behaviour]
		}

		this._initialize(particle, initializes)

		particle.addBehaviours(behaviours)
		particle.parent = this
		this.particles.push(particle)
	}

	_initialize(particle, initializes) {
		for (var i = 0, l = initializes.length; i < l; i++) {
			if (initializes[i] instanceof Initialize) {
				initializes[i].init(this, particle)
			} else {
				console.error('fuck this shit')
				setPrototypeByObject(particle, initialize)
				setVector2DByObject(particle, initialize)
			}
		}

		if (this.bindEmitter) {
			particle.p.add(this.p)
			particle.v.add(this.v)
			particle.a.add(this.a)
			particle.v.rotate(degreeTransform(this.rotation))
		}
	}

	/**
	 * Destory this Emitter
	 */
	destroy() {
		this.dead = true
		this.emitTotalTimes = -1
		if (this.particles.length == 0) {
			this.removeInitializers()
			this.removeAllBehaviours()

			if (this.parent)
				this.parent.removeEmitter(this)
		}
	}
}

import Emitter from './Emitter'

/**
 * The FollowEmitter class inherits from Proton.Emitter
 *
 * use the FollowEmitter will emit particle when mousemoving
 */
export default class FollowEmitter extends Emitter {
	/**
	 * @param {Element} mouseTarget mouseevent's target
	 * @param {Number} ease the easing of following speed
	 * @default 0.7
	 * @param {Object} pObj the parameters object
	 */
	constructor(mouseTarget = window, ease = .7, pObj) {
		super(pObj)
		this.mouseTarget = mouseTarget
		this.ease = ease
		this._allowEmitting = false
		this.initEventHandler()
	}

	initEventHandler() {
		var self = this
		this.mousemoveHandler = function(e) {
			self.mousemove.call(self, e)
		}
		this.mousedownHandler = function(e) {
			self.mousedown.call(self, e)
		}
		this.mouseupHandler = function(e) {
			self.mouseup.call(self, e)
		}

		this.mouseTarget.addEventListener('mousemove', this.mousemoveHandler, false)
	}

	/**
	 * start emit particle
	 */
	emit() {
		this._allowEmitting = true
	}

	/**
	 * stop emiting
	 */
	stopEmit() {
		this._allowEmitting = false
	}

	mousemove(e) {
		if (e.layerX || e.layerX == 0) {
			this.p.x += (e.layerX - this.p.x) * this.ease
			this.p.y += (e.layerY - this.p.y) * this.ease
		} else if (e.offsetX || e.offsetX == 0) {
			this.p.x += (e.offsetX - this.p.x) * this.ease
			this.p.y += (e.offsetY - this.p.y) * this.ease
		}
		if (this._allowEmitting) {
			super.emit('once')
		}
	}

	/**
	 * Destory this Emitter
	 */
	destroy() {
		super.destroy()
		this.mouseTarget.removeEventListener('mousemove', this.mousemoveHandler, false)
	}
}

import {setSpanValue} from './util'
import ColorSpan from './math/ColorSpan'
import PointZone from './zone/PointZone'
import {MEASURE} from './Proton'
import Polar2D from './math/Polar2D'

export class Initialize {
	constructor() {}

	initialize(target) {}
	reset() {}

	init(emitter, particle) {
		if (particle) {
			this.initialize(particle)
		} else {
			this.initialize(emitter)
		}
	}
}

export class ImageTarget extends Initialize {
	constructor(image, w = 20, h = w) {
		super()
		this.image = this.setSpanValue(image)
		this.w = w
		this.h = h
	}

	initialize(particle) {
		var imagetarget = this.image.getValue()
		if (typeof (imagetarget) === 'string') {
			particle.target = {
				width: this.w,
				height: this.h,
				src: imagetarget
			}
		} else {
			particle.target = imagetarget
		}
	}

	setSpanValue(color) {
		if ( color instanceof ColorSpan) {
			return color
		} else {
			return new ColorSpan(color)
		}
	}
}

export class Life extends Initialize {
	constructor(a, b, c) {
		super()
		this.lifePan = setSpanValue(a, b, c)
	}

	initialize(target) {
		if (this.lifePan.a === Infinity)
			target.life = Infinity
		else
			target.life = this.lifePan.getValue()
	}
}

export class Mass extends Initialize {
	constructor(a, b, c) {
		super()
		this.massPan = setSpanValue(a, b, c)
	}

	initialize(target) {
		target.mass = this.massPan.getValue()
	}
}

export class Position extends Initialize {
	constructor(zone) {
		super()
		this.zone = zone || new PointZone()
	}

	reset(zone) {
		this.zone = zone || new PointZone()
	}

	initialize(target) {
		this.zone.getPosition()
		target.p.x = this.zone.vector.x
		target.p.y = this.zone.vector.y
	}
}

export class Radius extends Initialize {
	constructor(a, b, c) {
		super()
		this.radius = setSpanValue(a, b, c)
	}

	reset(a, b, c) {
		this.radius = setSpanValue(a, b, c)
	}

	initialize(particle) {
		particle.radius = this.radius.getValue()
		particle.transform.oldRadius = particle.radius
	}
}

/**
 * The number of particles per second emission (a [particle]/b [s])
 * @param {Array or Number or Span} numpan the number of each emission
 * @param {Array or Number or Span} timepan the time of each emission
 * for example: new Rate(new Span(10, 20), new Span(.1, .25))
 */
export class Rate extends Initialize {
	constructor(numpan = 1, timepan = 1) {
		super()
		this.numPan = numpan
		this.timePan = timepan

		this.numPan = setSpanValue(this.numPan)
		this.timePan = setSpanValue(this.timePan)
		this.startTime = 0
		this.nextTime = 0
		this.init()
	}

	init() {
		this.startTime = 0
		this.nextTime = this.timePan.getValue()
	}

	getValue(time) {
		this.startTime += time
		if (this.startTime >= this.nextTime) {
			this.startTime = 0
			this.nextTime = this.timePan.getValue()
			if (this.numPan.b == 1) {
				if (this.numPan.getValue(false) > 0.5)
					return 1
				else
					return 0
			} else {
				return this.numPan.getValue(true)
			}
		}
		return 0
	}
}

export class Velocity extends Initialize {
	//radius and tha
	constructor(rpan, thapan, type = 'vector') {
		super()
		this.rPan = setSpanValue(rpan)
		this.thaPan = setSpanValue(thapan)
		this.type = type
	}

	reset(rpan, thapan, type = 'vector') {
		this.rPan = setSpanValue(rpan)
		this.thaPan = setSpanValue(thapan)
		this.type = type
	}

	normalizeVelocity(vr) {
		return vr * MEASURE
	}

	initialize(target) {
		if (this.type == 'p' || this.type == 'P' || this.type == 'polar') {
			var r = this.normalizeVelocity(this.rPan.getValue())
			var tha = this.thaPan.getValue() * Math.PI / 180
			var polar2d = new Polar2D(r, tha)
			target.v.x = polar2d.getX()
			target.v.y = polar2d.getY()
		} else {
			target.v.x = this.normalizeVelocity(this.rPan.getValue())
			target.v.y = this.normalizeVelocity(this.thaPan.getValue())
		}
	}
}

import * as ease from 'ease'
import Vector2D from './math/Vector2D'
import {getSpanValue} from './util'

/** @private */
export var PARTICLE_ID = 0

/**
 * the Particle class
 *
 * @param {Object} pObj the parameters object
 * for example {life:3,dead:false}
 */
export default class Particle {
	constructor(pOBJ) {
		/**
		 * The particle's id
		 * @property {String} id
		 */
		this.id = 'particle_' + PARTICLE_ID++
		this.reset(true)

		// setPrototypeByObject
		for (var key in pOBJ) {
			if (this.hasOwnProperty(key)) {
				this[singlePrototype] = getSpanValue(pOBJ[k])
			}
		}
	}

	getDirection() {
		return Math.atan2(this.v.x, -this.v.y) * (180 / Math.PI)
	}

	reset(init) {
		this.life = Infinity
		this.age = 0
		// 
		this.energy = 1
		this.dead = false
		this.sleep = false
		this.target = null
		this.sprite = null
		this.parent = null
		this.mass = 1
		this.radius = 10
		this.alpha = 1
		this.scale = 1
		this.rotation = 0
		this.color = null
		this.easing = ease.Linear

		if (init) {
			this.transform = {}
			this.p = new Vector2D()
			this.v = new Vector2D()
			this.a = new Vector2D()
			this.old = {
				p: new Vector2D(),
				v: new Vector2D(),
				a: new Vector2D(),
			}
			this.behaviours = []
		} else {
			for (var k in this.transform) {
				delete this.transform[k]
			}

			this.p.set(0, 0)
			this.v.set(0, 0)
			this.a.set(0, 0)
			this.old.p.set(0, 0)
			this.old.v.set(0, 0)
			this.old.a.set(0, 0)
			this.removeAllBehaviours()
		}

		this.transform.rgb = {
			r : 255,
			g : 255,
			b : 255,
		}
		return this
	}

	update(time, index) {
		if (!this.sleep) {
			this.age += time
			var behaviours = this.behaviours
			for (var i = 0, l = behaviours.length; i < l; i++) {
				if (behaviours[i]) {
					behaviours[i].applyBehaviour(this, time, index)
				}
			}
		} else {
		}

		if (this.age >= this.life) {
			this.destory()
		} else {
			var scale = this.easing(this.age, this.life)
			this.energy = Math.max(1 - scale, 0)
		}
	}

	addBehaviour(behaviour) {
		this.behaviours.push(behaviour)
		if (behaviour.hasOwnProperty('parents')) {
			behaviour.parents.push(this)
		}
		behaviour.initialize(this)
	}
	addBehaviours(behaviours) {
		for (var i = 0, l = behaviours.length; i < l; i++) {
			this.addBehaviour(behaviours[i])
		}
	}
	removeBehaviour(behaviour) {
		var index = this.behaviours.indexOf(behaviour)
		if (index > -1) {
			var behaviour = this.behaviours.splice(index, 1)
			behaviour.parents = null
		}
	}
	removeAllBehaviours() {
		this.behaviours.length = 0
	}

	/**
	 * Destory this particle
	 */
	destory() {
		this.removeAllBehaviours()
		this.energy = 0
		this.dead = true
		this.parent = null
	}
}

import Particle from './Particle'

export default class ParticlePool {
	constructor(num = 0, releaseTime = -1) {
		this.proParticleCount = num
		this.releaseTime = releaseTime

		this.poolList = []
		this.timeoutID = 0
		for (var i = 0; i < num; i++) {
			this.add()
		}
		//////////////////////////////
		if (this.releaseTime > 0) {
			this.timeoutID = setTimeout(this.release, this.releaseTime / 1000)
		}
	}

	create(newTypeParticleClass) {
		if (newTypeParticleClass)
			return new newTypeParticle()
		else
			return new Particle()
	}
	getCount() {
		return this.poolList.length
	}
	add() {
		return this.poolList.push(this.create())
	}
	get() {
		if (this.poolList.length === 0) {
			return this.create()
		} else {
			return this.poolList.pop().reset()
		}
	}
	set(particle) {
		if (this.poolList.length < Proton.POOL_MAX)
			return this.poolList.push(particle)
	}
	release() {
		var poolList = this.poolList
		for (var i = 0, l = poolList.length; i < l; i++) {
			if (poolList[i]['destory'])
				poolList[i].destory()
			delete poolList[i]
		}
		this.poolList.length = 0
	}
}

