export default class Alpha extends Behaviour {
	constructor(a, b, life, easing) {
		super(life, easing)
		this.reset(a, b)
	}

	/**
	 * @override
	 */
	reset(a = 1, b, life, easing) {
		this.same = (b === null || b === undefined)

		this.a = setSpanValue(a)
		this.b = setSpanValue(b)

		if(life) {
			super.reset(life, easing)
		}
	}

	/**
	 * @override
	 */
	initialize(particle) {
		particle.transform.alphaA = this.a.getValue()
		if (this.same) {
			particle.transform.alphaB = particle.transform.alphaA
		} else {
			particle.transform.alphaB = this.b.getValue()
		}
	}

	/**
	 * @override
	 */
	applyBehaviour(particle, time, index) {
		this._applyBehaviour(particle, time, index)
		var transform = particle.transform
		particle.alpha = transform.alphaB + (transform.alphaA - transform.alphaB) * this.energy
		if (particle.alpha < 0.001) {
			particle.alpha = 0
		}
	}
}

import Behaviour from '../Behaviour'

export default class Attraction extends Behaviour {
	constructor(targetPosition, force = 1, radius = 1000, life, easing) {
		super(life, easing)

		this.targetPosition = targetPosition || new Proton.Vector2D
		this.force = this.normalizeValue(force)
		this.radius = radius

		this.radiusSq = this.radius * this.radius
		this.attractionForce = new Proton.Vector2D()
		this.lengthSq = 0
	}

	/**
	 * @override
	 */
	reset(targetPosition, force = 1, radius = 1000, life, easing) {
		this.targetPosition = targetPosition || new Proton.Vector2D
		this.force = this.normalizeValue(force)
		this.radius = radius

		this.radiusSq = this.radius * this.radius
		this.attractionForce = new Proton.Vector2D()
		this.lengthSq = 0
		if (life) {
			super.reset(life, easing)
		}
	}

	/**
	 * @override
	 */
	applyBehaviour(particle, time, index) {
		this._applyBehaviour(particle, time, index)

		this.attractionForce.copy(this.targetPosition)
		this.attractionForce.sub(particle.p)

		this.lengthSq = this.attractionForce.lengthSq()
		if (this.lengthSq > 0.000004 && this.lengthSq < this.radiusSq) {
			this.attractionForce.normalize()
			this.attractionForce.multiplyScalar(1 - this.lengthSq / this.radiusSq)
			this.attractionForce.multiplyScalar(this.force)
			particle.a.add(this.attractionForce)
		}
	}
}

import Behaviour from '../Behaviour'

export default class Collision extends Behaviour {
	//can use Collision(emitter,true,function(){}) or Collision()
	constructor(emitter, mass, callback, life, easing) {
		super(life, easing)
		this.reset(emitter, mass, callback)
	}

	/**
	 * @override
	 */
	reset(emitter = null, mass = true, callback = null, life, easing) {
		this.emitter = emitter
		this.mass = mass
		this.callback = callback

		this.collisionPool = []
		this.delta = new Proton.Vector2D()
		if (life) {
			super.reset(life, easing)
		}
	}

	/**
	 * @override
	 */
	applyBehaviour(particle, time, index) {
		var newPool = this.emitter
			? this.emitter.particles.slice(index)
			: this.pool.slice(index)

		var otherParticle
		var averageMass1, averageMass2

		for (var i = 0, l = newPool.length; i < l; i++) {
			otherParticle = newPool[i]
			if (otherParticle !== particle) {
				this.delta.copy(otherParticle.p)
				this.delta.sub(particle.p)
				var lengthSq = this.delta.lengthSq()
				var distance = particle.radius + otherParticle.radius

				if (lengthSq <= distance * distance) {
					var overlap = distance - Math.sqrt(lengthSq)
					overlap += 0.5
					var totalMass = particle.mass + otherParticle.mass
					averageMass1 = this.mass
						? otherParticle.mass / totalMass
						: 0.5
					averageMass2 = this.mass
						? particle.mass / totalMass
						: 0.5
					particle.p.add(this.delta.clone().normalize().multiplyScalar(overlap * -averageMass1))
					otherParticle.p.add(this.delta.normalize().multiplyScalar(overlap * averageMass2))

					if (this.callback) {
						this.callback(particle, otherParticle)
					}
				}
			}
		}
	}
}

