export default class Polar2D {
	constructor(r = 0, tha = 0) {
		this.r = Math.abs(r)
		this.tha = tha
	}

	get x() { return this.r * Math.sin(this.tha) }
	get y() { return -this.r * Math.cos(this.tha) }

	getX() { return this.r * Math.sin(this.tha) }
	getY() { return -this.r * Math.cos(this.tha) }

	set(r, tha) {
		this.r = r
		this.tha = tha
		return this
	}
	setR(r) {
		this.r = r
		return this
	}
	setTha(tha) {
		this.tha = tha
		return this
	}

	copy(p) {
		this.r = p.r
		this.tha = p.tha
		return this
	}

	normalize() {
		this.r = 1
		return this
	}

	clear() {
		this.r = 0.0
		this.tha = 0.0
		return this
	}

	equals(v) {
		return (v.r === this.r) && (v.tha === this.tha)
	}

	clone() {
		return new Polar2D(this.r, this.tha)
	}

	toVector() { return new Vector2D(this.x, this.y) }
	toArray() { return [this.r, this.tha] }
}

export default class Rectangle {
	constructor(x, y, w, h) {
		this.x = x
		this.y = y
		this.width = w
		this.height = h
	}

	get top() { return this.y }
	get left() { return this.x }
	get bottom() { return this.y + this.height }
	get right() { return this.x + this.width }

	contains(x, y) {
		return x <= this.right && x >= this.x && y <= this.bottom && y >= this.y
	}
}

import {randomAToB} from '../util'

export default class Span {
	constructor(a = 1, b = a, center = false) {
		this.isArray = false

		if (Array.isArray(a)) {
			this.isArray = true
			this.a = a
		} else {
			this.a = a
			this.b = b
			this.center = center
		}
	}

	getValue(INT) {
		if (this.isArray) {
			return this.a[Math.floor(this.a.length * Math.random())]
		} else {
			if (!this.center) {
				return randomAToB(this.a, this.b, INT)
			} else {
				return randomAToB(this.a - this.b, this.a + this.b, INT)
			}
		}
	}
}

