export default class Vector2D {
	constructor(x = 0, y = 0) {
		this.x = x
		this.y = y
	}

	set(x, y) {
		this.x = x
		this.y = y
		return this
	}
	setX(x) {
		this.x = x
		return this
	}
	setY(y) {
		this.y = y
		return this
	}

	setComponent(index, value) {
		switch (index) {
			case 0:
				return this.x = value
			case 1:
				return this.y = value
		}
		throw new Error('index is out of range: ' + index)
	}
	getGradient() {
		if (this.x != 0)
			return Math.atan2(this.y, this.x)
		else if (this.y > 0)
			return Math.PI / 2
		else if (this.y < 0)
			return -Math.PI / 2
	}
	getComponent(index) {
		switch (index) {
			case 0:
				return this.x
			case 1:
				return this.y
		}
		throw new Error('index is out of range: ' + index)
	}

	copy(v) {
		this.x = v.x
		this.y = v.y
		return this
	}

	add(v, w) {
		if (w !== undefined) {
			return this.addVectors(v, w)
		}
		this.x += v.x
		this.y += v.y
		return this
	}
	addXY(a, b) {
		this.x += a
		this.y += b
		return this
	}
	addVectors(a, b) {
		this.x = a.x + b.x
		this.y = a.y + b.y
		return this
	}
	addScalar(s) {
		this.x += s
		this.y += s
		return this
	}
	sub(v, w) {
		if (w !== undefined) {
			return this.subVectors(v, w)
		}
		this.x -= v.x
		this.y -= v.y
		return this
	}
	subVectors(a, b) {
		this.x = a.x - b.x
		this.y = a.y - b.y
		return this
	}
	multiplyScalar(s) {
		this.x *= s
		this.y *= s
		return this
	}
	divideScalar(s) {
		if (s !== 0) {
			this.x /= s
			this.y /= s
		} else {
			this.set(0, 0)
		}
		return this
	}

	min(v) {
		if (this.x > v.x) {
			this.x = v.x
		}
		if (this.y > v.y) {
			this.y = v.y
		}
		return this
	}
	max(v) {
		if (this.x < v.x) {
			this.x = v.x
		}
		if (this.y < v.y) {
			this.y = v.y
		}
		return this
	}

	clamp(min, max) {
		// This function assumes min < max, if this assumption isn't true it will not operate correctly
		if (this.x < min.x) {
			this.x = min.x
		} else if (this.x > max.x) {
			this.x = max.x
		}
		if (this.y < min.y) {
			this.y = min.y
		} else if (this.y > max.y) {
			this.y = max.y
		}
		return this
	}
	negate() {
		return this.multiplyScalar(-1)
	}
	dot(v) {
		return this.x * v.x + this.y * v.y
	}

	lengthSq() {
		return this.x * this.x + this.y * this.y
	}
	length() {
		return Math.sqrt(this.x * this.x + this.y * this.y)
	}
	setLength(l) {
		var oldLength = this.length()
		if (oldLength !== 0 && l !== oldLength) {
			this.multiplyScalar(l / oldLength)
		}
	}

	normalize() {
		return this.divideScalar(this.length())
	}
	distanceTo(v) {
		return Math.sqrt(this.distanceToSquared(v))
	}
	rotate(tha) {
		var x = this.x
		var y = this.y
		this.x = x * Math.cos(tha) + y * Math.sin(tha)
		this.y = -x * Math.sin(tha) + y * Math.cos(tha)
		return this
	}
	distanceToSquared(v) {
		var dx = this.x - v.x, dy = this.y - v.y
		return dx * dx + dy * dy
	}
	lerp(v, alpha) {
		this.x += (v.x - this.x ) * alpha
		this.y += (v.y - this.y ) * alpha
		return this
	}

	equals(v) {
		return ((v.x === this.x ) && (v.y === this.y ) )
	}

	toArray() {
		return [this.x, this.y]
	}

	clear() {
		this.x = 0.0
		this.y = 0.0
		return this
	}

	clone() {
		return new Vector2D(this.x, this.y)
	}
}

import {
	PROTON_UPDATE, PROTON_UPDATE_AFTER,
	EMITTER_ADDED, EMITTER_REMOVED,
	PARTICLE_CREATED, PARTICLE_UPDATE, PARTICLE_DEAD
} from '../Proton'

export default class BaseRender {
	constructor(proton, element, stroke) {
		this.proton = proton
		this.element = element
		this.stroke = stroke
	}

	start() {
		this.proton.on(PROTON_UPDATE, () => {
			this.onProtonUpdate()
		})

		this.proton.on(PROTON_UPDATE_AFTER, () => {
			this.onProtonUpdateAfter()
		})

		this.proton.on(EMITTER_ADDED, (emitter) => {
			this.onEmitterAdded(emitter)
		})

		this.proton.on(EMITTER_REMOVED, (emitter) => {
			this.onEmitterRemoved(emitter)
		})

		var length = this.proton.emitters.length, i
		for ( i = 0; i < length; i++) {
			var emitter = this.proton.emitters[i]
			this.addEmitterListener(emitter)
		}
	}

	/** @abstract */
	resize(width, height) {}

	addEmitterListener(emitter) {
		emitter.addEventListener(PARTICLE_CREATED, (particle) => {
			this.onParticleCreated(particle)
		})
		emitter.addEventListener(PARTICLE_UPDATE, (particle) => {
			this.onParticleUpdate(particle)
		})
		emitter.addEventListener(PARTICLE_DEAD, (particle) => {
			this.onParticleDead(particle)
		})
	}

	stop() {
		var length = this.proton.emitters.length
		this.proton.removeAllEventListeners()
		for (var i = 0; i < length; i++) {
			var emitter = this.proton.emitters[i]
			emitter.removeAllEventListeners()
		}
	}

	/** @abstract */
	onEmitterAdded(emitter) {
		this.addEmitterListener(emitter)
	}

	/** @abstract */
	onEmitterRemoved(emitter) {
		emitter.removeAllEventListeners()
	}

	/** @abstract */
	onProtonUpdate() {}
	/** @abstract */
	onProtonUpdateAfter() {}
	/** @abstract */
	onParticleCreated(particle) {}
	/** @abstract */
	onParticleUpdate(particle) {}
	/** @abstract */
	onParticleDead(particle) {}
}

import BaseRender from './BaseRender'
import {getImage, rgbToHex, degreeTransform} from '../util'

export default class CanvasRender extends BaseRender {
	constructor(proton, element) {
		super(proton, element)
		this.stroke = null
		this.context = this.element.getContext('2d')
		this.bufferCache = {}
	}

	/** @override */
	resize(width, height) {
		this.element.width = width
		this.element.height = height
	}
	start() {
		super.start()
	}

	setStroke(color = '#000000', thinkness = 1) {
		this.stroke = {
			color: color,
			thinkness: thinkness,
		}
	}

	/** @override */
	onProtonUpdate() {
		this.context.clearRect(0, 0, this.element.width, this.element.height)
	}

	/** @override */
	onParticleCreated(particle) {
		if (particle.target)
			getImage(particle.target, particle, false)
		else
			particle.color = particle.color ? particle.color : '#ff0000'
	}

	/** @override */
	onParticleUpdate(particle) {
		if (particle.target) {
			if (particle.target instanceof Image) {
				var w = particle.target.width * particle.scale | 0
				var h = particle.target.height * particle.scale | 0
				var x = particle.p.x - w / 2
				var y = particle.p.y - h / 2

				if (!!particle.color) {
					if (!particle.transform['buffer'])
						particle.transform.buffer = this.getBuffer(particle.target)
					var bufferContext = particle.transform.buffer.getContext('2d')
					bufferContext.clearRect(0, 0, particle.transform.buffer.width, particle.transform.buffer.height)
					bufferContext.globalAlpha = particle.alpha
					bufferContext.drawImage(particle.target, 0, 0)
					bufferContext.globalCompositeOperation = 'source-atop'
					bufferContext.fillStyle = rgbToHex(particle.transform.rgb)
					bufferContext.fillRect(0, 0, particle.transform.buffer.width, particle.transform.buffer.height)
					bufferContext.globalCompositeOperation = 'source-over'
					bufferContext.globalAlpha = 1
					this.context.drawImage(particle.transform.buffer, 0, 0, particle.transform.buffer.width, particle.transform.buffer.height, x, y, w, h)
				} else {
					this.context.save()
					this.context.globalAlpha = particle.alpha
					this.context.translate(particle.p.x, particle.p.y)
					this.context.rotate(degreeTransform(particle.rotation))
					this.context.translate(-particle.p.x, -particle.p.y)
					this.context.drawImage(particle.target, 0, 0, particle.target.width, particle.target.height, x, y, w, h)
					this.context.globalAlpha = 1
					this.context.restore()
				}
			}
		} else {
			if (particle.transform['rgb'])
				this.context.fillStyle = 'rgba(' + particle.transform.rgb.r + ',' + particle.transform.rgb.g + ',' + particle.transform.rgb.b + ',' + particle.alpha + ')'
			else
				this.context.fillStyle = particle.color
			this.context.beginPath()
			this.context.arc(particle.p.x, particle.p.y, particle.radius, 0, Math.PI * 2, true)
			if (this.stroke) {
				this.context.strokeStyle = this.stroke.color
				this.context.lineWidth = this.stroke.thinkness
				this.context.stroke()
			}

			this.context.closePath()
			this.context.fill()
		}
	}

	/** @override */
	onParticleDead(particle) {}

	getBuffer(image) {
		if ( image instanceof Image) {
			var size = image.width + '_' + image.height
			var canvas = this.bufferCache[size]
			if (!canvas) {
				canvas = document.createElement('canvas')
				canvas.width = image.width
				canvas.height = image.height
				this.bufferCache[size] = canvas
			}
			return canvas
		}
	}
}

import BaseRender from './BaseRender'

export default class DomRender extends BaseRender {
	constructor(proton, element) {
		super(proton, element)
		this.stroke = null
	}

	/** @override */
	start() {
		super.start()
	}

	setStroke(color = '#000000', thinkness = 1) {
		this.stroke = {
			color: color,
			thinkness: thinkness,
		}
	}

	/** @override */
	onProtonUpdate() {
	}

	/** @override */
	onParticleCreated(particle) {
		if (particle.target) {
			var self = this
			Proton.Util.getImage(particle.target, particle, false, function(particle) {
				self.setImgInDIV.call(self, particle)
			})
		} else {
			particle.transform.canvas = Proton.DomUtil.createCanvas(particle.id + '_canvas', particle.radius + 1, particle.radius + 1, 'absolute')
			particle.transform.bakOldRadius = particle.radius

			if (this.stroke) {
				particle.transform.canvas.width = 2 * particle.radius + this.stroke.thinkness * 2
				particle.transform.canvas.height = 2 * particle.radius + this.stroke.thinkness * 2
			} else {
				particle.transform.canvas.width = 2 * particle.radius + 1
				particle.transform.canvas.height = 2 * particle.radius + 1
			}

			particle.transform.context = particle.transform.canvas.getContext('2d')
			particle.transform.context.fillStyle = particle.color
			particle.transform.context.beginPath()
			particle.transform.context.arc(particle.radius, particle.radius, particle.radius, 0, Math.PI * 2, true)

			if (this.stroke) {
				particle.transform.context.strokeStyle = this.stroke.color
				particle.transform.context.lineWidth = this.stroke.thinkness
				particle.transform.context.stroke()
			}

			particle.transform.context.closePath()
			particle.transform.context.fill()
			this.element.appendChild(particle.transform.canvas)
		}

	}

	/** @override */
	onParticleUpdate(particle) {
		if (particle.target) {
			if (particle.target instanceof Image) {
				particle.transform.canvas.style.opacity = particle.alpha
				Proton.DomUtil.transformDom(particle.transform.canvas, particle.p.x - particle.target.width / 2, particle.p.y - particle.target.height / 2, particle.scale, particle.rotation)
			}
		} else {
			particle.transform.canvas.style.opacity = particle.alpha
			if (particle.transform['oldRadius'])
				Proton.DomUtil.transformDom(particle.transform.canvas, particle.p.x - particle.transform.oldRadius, particle.p.y - particle.transform.oldRadius, particle.scale, particle.rotation)
			else
				Proton.DomUtil.transformDom(particle.transform.canvas, particle.p.x - particle.transform.bakOldRadius, particle.p.y - particle.transform.bakOldRadius, particle.scale, particle.rotation)
		}
	}

	/** @override */
	onParticleDead(particle) {
		if (particle.transform.canvas)
			this.element.removeChild(particle.transform.canvas)
	}

	setImgInDIV(particle) {
		particle.transform.canvas = Proton.DomUtil.createCanvas(particle.id + '_canvas', particle.target.width + 1, particle.target.height + 1, 'absolute', particle.p.x - particle.radius, particle.p.y - particle.radius)
		particle.transform.context = particle.transform.canvas.getContext('2d')
		particle.transform.context.drawImage(particle.target, 0, 0, particle.target.width, particle.target.height)
		this.element.appendChild(particle.transform.canvas)
	}
}

import BaseRender from './BaseRender'

export default class EaselRender extends BaseRender {
	constructor(proton, element, stroke) {
		super(proton, element)
		this.stroke = stroke
	}

	/** @override */
	resize(width, height) {

	}
	/** @override */
	start() {
		EaselRender._super_.prototype.start.call(this);
	}

	/** @override */
	onProtonUpdate() {

	}

	/** @override */
	onParticleCreated(particle) {
		if (particle.target) {
			particle.target = particle.target.clone();
			if (!particle.target.parent) {
				if (!!particle.target['image']) {
					particle.target.regX = particle.target.image.width / 2;
					particle.target.regY = particle.target.image.height / 2;
				}
				this.element.addChild(particle.target);
			}
		} else {
			var graphics = new createjs.Graphics();
			if (this.stroke) {
				if (this.stroke == true) {
					graphics.beginStroke('#000000');
				} else if (this.stroke instanceof String) {
					graphics.beginStroke(this.stroke);
				}
			}

			graphics.beginFill(particle.color).drawCircle(0, 0, particle.radius);
			var shape = new createjs.Shape(graphics);
			particle.target = shape;
			this.element.addChild(particle.target);
		}
	}

	/** @override */
	onParticleUpdate(particle) {
		if (particle.target) {
			particle.target.x = particle.p.x;
			particle.target.y = particle.p.y;
			particle.target.alpha = particle.alpha;
			particle.target.scaleX = particle.target.scaleY = particle.scale;
			particle.target.rotation = particle.rotation;
		}
	}

	/** @override */
	onParticleDead(particle) {
		if (particle.target) {
			if (particle.target.parent)
				particle.target.parent.removeChild(particle.target);
		}
	}
}

import BaseRender from './BaseRender'

export default class PixelRender extends BaseRender {
	constructor(proton, element, rectangle) {
		super(proton, element);
		this.context = this.element.getContext('2d');
		this.imageData = null;
		this.rectangle = null;
		this.rectangle = rectangle;
		this.createImageData(rectangle);
	}

	/** @override */
	resize(width, height) {
		this.element.width = width;
		this.element.height = height;
	}
	createImageData(rectangle) {
		if (!rectangle)
			this.rectangle = new Proton.Rectangle(0, 0, this.element.width, this.element.height);
		else
			this.rectangle = rectangle;
		this.imageData = this.context.createImageData(this.rectangle.width, this.rectangle.height);
		this.context.putImageData(this.imageData, this.rectangle.x, this.rectangle.y);
	}

	/** @override */
	start() {
		PixelRender._super_.prototype.start.call(this);
	};

	/** @override */
	onProtonUpdate() {
		this.context.clearRect(this.rectangle.x, this.rectangle.y, this.rectangle.width, this.rectangle.height);
		this.imageData = this.context.getImageData(this.rectangle.x, this.rectangle.y, this.rectangle.width, this.rectangle.height);
	}

	/** @override */
	onProtonUpdateAfter() {
		this.context.putImageData(this.imageData, this.rectangle.x, this.rectangle.y);
	}

	/** @override */
	onParticleCreated(particle) {

	}

	/** @override */
	onParticleUpdate(particle) {
		if (this.imageData) {
			this.setPixel(this.imageData, Math.floor(particle.p.x - this.rectangle.x), Math.floor(particle.p.y - this.rectangle.y), particle);
		}
	}

	setPixel(imagedata, x, y, particle) {
		var rgb = particle.transform.rgb;
		if ((x < 0) || (x > this.element.width) || (y < 0) || (y > this.elementwidth))
			return;

		var i = ((y >> 0) * imagedata.width + (x >> 0)) * 4;

		imagedata.data[i] = rgb.r;
		imagedata.data[i + 1] = rgb.g;
		imagedata.data[i + 2] = rgb.b;
		imagedata.data[i + 3] = particle.alpha * 255;
	}

	/** @override */
	onParticleDead(particle) {

	}
}

import BaseRender from './BaseRender'

var PI_180 = Math.PI / 180

var Sprite = global.PIXI && PIXI.Sprite
var Graphics = global.PIXI && PIXI.Graphics

export default class PixiRender extends BaseRender {
	constructor(proton, element, stroke) {
		super(proton, element)
		this.stroke = stroke
	}

	/** @override */
	resize (width, height) {
	}

	/** @override */
	start () {
		super.start()
	}

	/** @override */
	onProtonUpdate () {
	}

	/**
	 * @override
	 * @param particle
	 */
	onParticleCreated (particle) {
		if (particle.target) {
			particle.target = new Sprite(particle.target)
			particle.target.anchor.x = 0.5
			particle.target.anchor.y = 0.5
			this.element.addChild(particle.target)
		} else {
			var graphics = new Graphics()
			if (this.stroke) {
				if (this.stroke === true) {
					graphics.beginStroke('#000000')
				} else if (this.stroke instanceof String) {
					graphics.beginStroke(this.stroke)
				}
			}
			graphics.beginFill(particle.color)
			graphics.drawCircle(0, 0, particle.radius)
			particle.target = graphics
			this.element.addChild(particle.target)
		}
	}

	/**
	 * @override
	 * @param particle
	 */
	onParticleUpdate (particle) {
		if (particle.target) {
			particle.target.position.x = particle.p.x
			particle.target.position.y = particle.p.y
			particle.target.alpha = particle.alpha
			particle.target.scale.x = particle.scale
			particle.target.scale.y = particle.scale
			// using cached version of Math.PI / 180 for slight performance increase.
			particle.target.rotation = particle.rotation * PI_180
		}
	}

	/**
	 * @override
	 * @param particle
	 */
	onParticleDead (particle) {
		if (particle.target) {
			this.element.removeChild(particle.target)
		}
	}
}

import PixiRender from './PixiRender'
import DomRender from './DomRender'
import CanvasRender from './CanvasRender'
import WebGLRender from './WebGLRender'
import EaselRender from './EaselRender'
import PixelRender from './PixelRender'
import BaseRender from './BaseRender'

//the own renderer
export default class Renderer {
	constructor(type = 'canvas', proton, element) {
		///element dom/div canvas/canvas easeljs/cantainer(or stage)
		this.element = element
		this.type = type
		this.proton = proton
		this.renderer = this.createRenderer()
	}

	start() {
		this.addEventHandler()
		this.renderer.start()
	}
	stop() {
		this.renderer.stop()
	}

	resize(width, height) {
		this.renderer.resize(width, height)
	}

	setStroke(color, thinkness) {
		if (this.renderer.hasOwnProperty('stroke'))
			this.renderer.setStroke(color, thinkness)
		else
			alert('Sorry this renderer do not suppest stroke method!')
	}

	createImageData(data) {
		if (this.renderer instanceof PixelRender)
			this.renderer.createImageData(data)
	}

	setMaxRadius(radius) {
		if (this.renderer instanceof WebGLRender)
			this.renderer.setMaxRadius(radius)
	}
	blendEquation(A) {
		if (this.renderer instanceof WebGLRender)
			this.renderer.blendEquation(A)
	}
	blendFunc(A, B) {
		if (this.renderer instanceof WebGLRender)
			this.renderer.blendFunc(A, B)
	}

	setType(type) {
		this.type = type
		this.renderer = this.getRenderer()
	}

	createRenderer() {
		switch(this.type) {
		case 'pixi':
			return new PixiRender(this.proton, this.element)
		case 'dom':
			return new DomRender(this.proton, this.element)
		case 'canvas':
			return new CanvasRender(this.proton, this.element)
		case 'webgl':
			return new WebGLRender(this.proton, this.element)
		case 'easel':
			return new EaselRender(this.proton, this.element)
		case 'easeljs':
			return new EaselRender(this.proton, this.element)
		case 'pixel':
			return new PixelRender(this.proton, this.element)
		default:
			return new BaseRender(this.proton, this.element)
		}
	}

	render(callback) {
		this.renderer.render(callback)
	}

	addEventHandler() {
		if (this.onProtonUpdate)
			this.renderer.onProtonUpdate = this.onProtonUpdate

		if (this.onParticleCreated)
			this.renderer.onParticleCreated = this.onParticleCreated

		if (this.onParticleUpdate)
			this.renderer.onParticleUpdate = this.onParticleUpdate

		if (this.onParticleDead)
			this.renderer.onParticleDead = this.onParticleDead
	}
}

import BaseRender from './BaseRender'

import Mat3 from '../math/Mat3'
import {
	ipot,nhpot,
	//makeTranslation,makeRotation, makeScale,
	//matrixMultiply,
	getImage,
	createCanvas,
} from '../util'

function makeTranslation(tx, ty) {
	return [1, 0, 0, 0, 1, 0, tx, ty, 1]
}

function makeRotation(angleInRadians) {
	var c = Math.cos(angleInRadians)
	var s = Math.sin(angleInRadians)
	return [c, -s, 0, s, c, 0, 0, 0, 1]
}

function makeScale(sx, sy) {
	return [sx, 0, 0, 0, sy, 0, 0, 0, 1]
}

function matrixMultiply(a, b) {
	var a00 = a[0] // 0 * 3 + 0
	var a01 = a[1] // 0 * 3 + 1
	var a02 = a[2] // 0 * 3 + 2
	var a10 = a[3] // 1 * 3 + 0
	var a11 = a[4] // 1 * 3 + 1
	var a12 = a[5] // 1 * 3 + 2
	var a20 = a[6] // 2 * 3 + 0
	var a21 = a[7] // 2 * 3 + 1
	var a22 = a[8] // 2 * 3 + 2

	var b00 = b[0] // 0 * 3 + 0
	var b01 = b[1] // 0 * 3 + 1
	var b02 = b[2] // 0 * 3 + 2
	var b10 = b[3] // 1 * 3 + 0
	var b11 = b[4] // 1 * 3 + 1
	var b12 = b[5] // 1 * 3 + 2
	var b20 = b[6] // 2 * 3 + 0
	var b21 = b[7] // 2 * 3 + 1
	var b22 = b[8] // 2 * 3 + 2

	return [
		a00 * b00 + a01 * b10 + a02 * b20,
		a00 * b01 + a01 * b11 + a02 * b21,
		a00 * b02 + a01 * b12 + a02 * b22,
		a10 * b00 + a11 * b10 + a12 * b20,
		a10 * b01 + a11 * b11 + a12 * b21,
		a10 * b02 + a11 * b12 + a12 * b22,
		a20 * b00 + a21 * b10 + a22 * b20,
		a20 * b01 + a21 * b11 + a22 * b21,
		a20 * b02 + a21 * b12 + a22 * b22,
	]
}


class MStack {
	constructor() {
		this.mats = [];
		this.size = 0;
		for (var i = 0; i < 20; i++)
			this.mats.push(Mat3.create([0, 0, 0, 0, 0, 0, 0, 0, 0]))
	}


	set(m, i) {
		if (i == 0)
			Mat3.set(m, this.mats[0])
		else
			Mat3.multiply(this.mats[i - 1], m, this.mats[i])
		this.size = Math.max(this.size, i + 1)
	}

	push(m) {
		if (this.size == 0)
			Mat3.set(m, this.mats[0])
		else
			Mat3.multiply(this.mats[this.size - 1], m, this.mats[this.size])
		this.size++
	}

	pop() {
		if (this.size > 0)
			this.size--
	}

	top() {
		return (this.mats[this.size - 1])
	}
}

export default class WebGLRender extends BaseRender {
	constructor(proton, element) {
		super(proton, element);
		this.gl = this.element.getContext('experimental-webgl', {
			antialias : true,
			stencil : false,
			depth : false
		});
		if (!this.gl)
			alert('Sorry your browser do not suppest WebGL!');
		this.initVar();
		this.setMaxRadius();
		this.initShaders();
		this.initBuffers();
		this.gl.blendEquation(this.gl.FUNC_ADD);
		this.gl.blendFunc(this.gl.SRC_ALPHA, this.gl.ONE_MINUS_SRC_ALPHA);
		this.gl.enable(this.gl.BLEND);
	}

	resize(width, height) {
		this.umat[4] = -2;
		this.umat[7] = 1;
		this.smat[0] = 1 / width;
		this.smat[4] = 1 / height;
		this.mstack.set(this.umat, 0);
		this.mstack.set(this.smat, 1);
		this.gl.viewport(0, 0, width, height);
		this.element.width = width;
		this.element.height = height;
	}

	setMaxRadius(radius) {
		this.circleCanvasURL = this.createCircle(radius);
	}

	getVertexShader() {
		var vsSource = [
			'uniform vec2 viewport;',
			'attribute vec2 aVertexPosition;',
			'attribute vec2 aTextureCoord;',
			'uniform mat3 tMat;',
			'varying vec2 vTextureCoord;',
			'varying float alpha;',
			'void main() {',
			'  vec3 v = tMat * vec3(aVertexPosition, 1.0);',
			'  gl_Position = vec4(v.x, v.y, 0, 1);',
			'  vTextureCoord = aTextureCoord;',
			'  alpha = tMat[0][2];',
			'}',
		].join('\n');
		return vsSource;
	}

	getFragmentShader() {
		var fsSource = [
			'precision mediump float;',
			'varying vec2 vTextureCoord;',
			'varying float alpha;',
			'uniform sampler2D uSampler;',
			'uniform vec4 color;',
			'uniform bool useTexture;',
			'uniform vec3 uColor;',
			'void main() {',
			'  vec4 textureColor = texture2D(uSampler, vTextureCoord);',
			'  gl_FragColor = textureColor * vec4(uColor, 1.0);',
			'  gl_FragColor.w *= alpha;',
			'}',
		].join('\n');
		return fsSource;
	}

	initVar() {
		this.mstack = new MStack();
		this.umat = Mat3.create([2, 0, 1, 0, -2, 0, -1, 1, 1]);
		this.smat = Mat3.create([1 / 100, 0, 1, 0, 1 / 100, 0, 0, 0, 1]);
		this.texturebuffers = {};
	}

	start() {
		super.start()
		this.resize(this.element.width, this.element.height);
	}

	blendEquation(A) {
		var gl = this.gl
		gl.blendEquation(gl[A]);
	}

	blendFunc(A, B) {
		var gl = this.gl
		gl.blendFunc(gl[A], gl[B]);
	}

	getShader(gl, str, fs) {
		var shader;
		if (fs)
			shader = gl.createShader(gl.FRAGMENT_SHADER);
		else
			shader = gl.createShader(gl.VERTEX_SHADER);
		gl.shaderSource(shader, str);
		gl.compileShader(shader);

		if (!gl.getShaderParameter(shader, gl.COMPILE_STATUS)) {
			alert(gl.getShaderInfoLog(shader));
			return null;
		}
		return shader;
	}

	initShaders() {
		var gl = this.gl
		var fragmentShader = this.getShader(gl, this.getFragmentShader(), true);
		var vertexShader = this.getShader(gl, this.getVertexShader(), false);

		this.sprogram = gl.createProgram();
		gl.attachShader(this.sprogram, vertexShader);
		gl.attachShader(this.sprogram, fragmentShader);
		gl.linkProgram(this.sprogram);
		if (!gl.getProgramParameter(this.sprogram, gl.LINK_STATUS))
			alert('Could not initialise shaders');

		gl.useProgram(this.sprogram);
		this.sprogram.vpa = gl.getAttribLocation(this.sprogram, 'aVertexPosition');
		this.sprogram.tca = gl.getAttribLocation(this.sprogram, 'aTextureCoord');
		gl.enableVertexAttribArray(this.sprogram.tca);
		gl.enableVertexAttribArray(this.sprogram.vpa);

		this.sprogram.tMatUniform = gl.getUniformLocation(this.sprogram, 'tMat');
		this.sprogram.samplerUniform = gl.getUniformLocation(this.sprogram, 'uSampler');
		this.sprogram.useTex = gl.getUniformLocation(this.sprogram, 'useTexture');
		this.sprogram.color = gl.getUniformLocation(this.sprogram, 'uColor');
		gl.uniform1i(this.sprogram.useTex, 1);
	}

	initBuffers() {
		var gl = this.gl
		this.unitIBuffer = gl.createBuffer();
		gl.bindBuffer(gl.ELEMENT_ARRAY_BUFFER, this.unitIBuffer);
		var vs = [0, 3, 1, 0, 2, 3];
		gl.bufferData(gl.ELEMENT_ARRAY_BUFFER, new Uint16Array(vs), gl.STATIC_DRAW);

		var ids = [];
		for (var i = 0; i < 100; i++)
			ids.push(i);

		var idx

		idx = new Uint16Array(ids);
		this.unitI33 = gl.createBuffer();
		gl.bindBuffer(gl.ELEMENT_ARRAY_BUFFER, this.unitI33);
		gl.bufferData(gl.ELEMENT_ARRAY_BUFFER, idx, gl.STATIC_DRAW);

		ids = [];
		for ( i = 0; i < 100; i++)
			ids.push(i, i + 1, i + 2);

		idx = new Uint16Array(ids);
		this.stripBuffer = gl.createBuffer();
		gl.bindBuffer(gl.ELEMENT_ARRAY_BUFFER, this.stripBuffer);
		gl.bufferData(gl.ELEMENT_ARRAY_BUFFER, idx, gl.STATIC_DRAW);
	}

	createCircle(raidus = 32) {
		this.circleCanvasRadius = nhpot(raidus);
		var canvas = createCanvas('circle_canvas', this.circleCanvasRadius * 2, this.circleCanvasRadius * 2);
		var context = canvas.getContext('2d');
		context.beginPath();
		context.arc(this.circleCanvasRadius, this.circleCanvasRadius, this.circleCanvasRadius, 0, Math.PI * 2, true);
		context.closePath();
		context.fillStyle = '#FFF';
		context.fill();
		return canvas.toDataURL();
	}

	setImgInCanvas(particle) {
		var _w = particle.target.width;
		var _h = particle.target.height;
		var _width = nhpot(particle.target.width);
		var _height = nhpot(particle.target.height);
		var _scaleX = particle.target.width / _width;
		var _scaleY = particle.target.height / _height;

		var gl = this.gl

		if (!this.texturebuffers[particle.transform.src])
			this.texturebuffers[particle.transform.src] = [gl.createTexture(), gl.createBuffer(), gl.createBuffer()];
		particle.transform.texture = this.texturebuffers[particle.transform.src][0];
		particle.transform.vcBuffer = this.texturebuffers[particle.transform.src][1];
		particle.transform.tcBuffer = this.texturebuffers[particle.transform.src][2];
		gl.bindBuffer(gl.ARRAY_BUFFER, particle.transform.tcBuffer);
		gl.bufferData(gl.ARRAY_BUFFER, new Float32Array([0.0, 0.0, _scaleX, 0.0, 0.0, _scaleY, _scaleY, _scaleY]), gl.STATIC_DRAW);
		gl.bindBuffer(gl.ARRAY_BUFFER, particle.transform.vcBuffer);
		gl.bufferData(gl.ARRAY_BUFFER, new Float32Array([0.0, 0.0, _w, 0.0, 0.0, _h, _w, _h]), gl.STATIC_DRAW);

		var context = particle.transform.canvas.getContext('2d');
		var data = context.getImageData(0, 0, _width, _height);

		gl.bindTexture(gl.TEXTURE_2D, particle.transform.texture);
		gl.texImage2D(gl.TEXTURE_2D, 0, gl.RGBA, gl.RGBA, gl.UNSIGNED_BYTE, data);
		gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_MAG_FILTER, gl.LINEAR);
		gl.texParameteri(gl.TEXTURE_2D, gl.TEXTURE_MIN_FILTER, gl.LINEAR_MIPMAP_NEAREST);
		gl.generateMipmap(gl.TEXTURE_2D);
		particle.transform.textureLoaded = true;
		particle.transform.textureWidth = _w;
		particle.transform.textureHeight = _h;
	}

	setStroke(color, thinkness) {

	}

	onProtonUpdate() {
		//this.gl.clearColor(0, 0, 0, 1);
		//this.gl.clear(this.gl.COLOR_BUFFER_BIT | this.gl.DEPTH_BUFFER_BIT);
	}

	onParticleCreated(particle) {
		particle.transform.textureLoaded = false;
		particle.transform.tmat = Mat3.create();
		particle.transform.tmat[8] = 1;
		particle.transform.imat = Mat3.create();
		particle.transform.imat[8] = 1;
		if (particle.target) {
			getImage(particle.target, particle, true, (particle) => {
				this.setImgInCanvas(particle);
				particle.transform.oldScale = 1;
			});
		} else {
			getImage(this.circleCanvasURL, particle, true, (particle) => {
				this.setImgInCanvas(particle);
				particle.transform.oldScale = particle.radius / this.circleCanvasRadius;
			});
		}
	}

	onParticleUpdate(particle) {
		if (particle.transform.textureLoaded) {
			this.updateMatrix(particle);
			var gl = this.gl
			gl.uniform3f(this.sprogram.color, particle.transform.rgb.r / 255, particle.transform.rgb.g / 255, particle.transform.rgb.b / 255);
			gl.uniformMatrix3fv(this.sprogram.tMatUniform, false, this.mstack.top());
			gl.bindBuffer(gl.ARRAY_BUFFER, particle.transform.vcBuffer);
			gl.vertexAttribPointer(this.sprogram.vpa, 2, gl.FLOAT, false, 0, 0);
			gl.bindBuffer(gl.ARRAY_BUFFER, particle.transform.tcBuffer);
			gl.vertexAttribPointer(this.sprogram.tca, 2, gl.FLOAT, false, 0, 0);
			gl.bindTexture(gl.TEXTURE_2D, particle.transform.texture);
			gl.uniform1i(this.sprogram.samplerUniform, 0);
			gl.bindBuffer(gl.ELEMENT_ARRAY_BUFFER, this.unitIBuffer);
			gl.drawElements(gl.TRIANGLES, 6, gl.UNSIGNED_SHORT, 0);
			this.mstack.pop();
		}
	}

	onParticleDead(particle) {

	}

	updateMatrix(particle) {
		var moveOriginMatrix = makeTranslation(-particle.transform.textureWidth / 2, -particle.transform.textureHeight / 2);
		var translationMatrix = makeTranslation(particle.p.x, particle.p.y);
		var angel = particle.rotation * (Math.PI / 180);
		var rotationMatrix = makeRotation(angel);
		var scale = particle.scale * particle.transform.oldScale;
		var scaleMatrix = makeScale(scale, scale);

		var matrix = matrixMultiply(moveOriginMatrix, scaleMatrix);
		matrix = matrixMultiply(matrix, rotationMatrix);
		matrix = matrixMultiply(matrix, translationMatrix);

		Mat3.inverse(matrix, particle.transform.imat);
		matrix[2] = particle.alpha;
		this.mstack.push(matrix);
	}
}

import Span from './math/Span'

export function setSpanValue(a, b, c) {
	if (a instanceof Span) {
		return a
	} else {
		if (!b) {
			return new Span(a)
		} else {
			if (!c)
				return new Span(a, b)
			else
				return new Span(a, b, c)
		}
	}
}

export function getSpanValue(pan) {
	if (pan instanceof Span)
		return pan.getValue()
	else
		return pan
}

export function randomAToB(a, b, INT) {
	if (!INT)
		return a + Math.random() * (b - a )
	else
		return Math.floor(Math.random() * (b - a)) + a
}

var PI_180 = Math.PI / 180
export function degreeTransform(a) {
	return a * PI_180
}

export function hexToRGB(h) {
	var hex16 = (h.charAt(0) == "#") ? h.substring(1, 7) : h
	var r = parseInt(hex16.substring(0, 2), 16)
	var g = parseInt(hex16.substring(2, 4), 16)
	var b = parseInt(hex16.substring(4, 6), 16)

	return {r, g, b}
}

export function rgbToHex(rbg) {
	return 'rgb(' + rbg.r + ', ' + rbg.g + ', ' + rbg.b + ')'
}

export function getImage(img, particle, drawCanvas, fun) {
	if ( typeof (img) == 'string') {
		loadAndSetImage(img, particle, drawCanvas, fun)
	} else if ( typeof (img) == 'object') {
		loadAndSetImage(img.src, particle, drawCanvas, fun)
	} else if ( img instanceof Image) {
		loadedImage(img.src, particle, drawCanvas, fun, img)
	}
}

export var TextureBuffer = {}
export var TextureCanvasBuffer = {}

export function loadedImage(src, particle, drawCanvas, fun, target) {
	particle.target = target
	particle.transform.src = src
	if (!TextureBuffer[src])
		TextureBuffer[src] = particle.target
	if (drawCanvas) {
		if (TextureCanvasBuffer[src]) {
			particle.transform.canvas = TextureCanvasBuffer[src]
		} else {
			var _width = nhpot(particle.target.width)
			var _height = nhpot(particle.target.height)
			particle.transform.canvas = createCanvas('canvas' + src, _width, _height)
			var context = particle.transform.canvas.getContext('2d')
			context.drawImage(particle.target, 0, 0, particle.target.width, particle.target.height)
			TextureCanvasBuffer[src] = particle.transform.canvas
		}
	}
	if (fun)
		fun(particle)
}

export function loadAndSetImage(src, particle, drawCanvas, fun) {
	if (TextureBuffer[src]) {
		loadedImage(src, particle, drawCanvas, fun, TextureBuffer[src])
	} else {
		var myImage = new Image()
		myImage.onload = function(e) {
			loadedImage(src, particle, drawCanvas, fun, e.target)
		}
		myImage.src = src
	}
}

export function ipot(length) {
	return (length & (length - 1)) == 0
}

export function nhpot(length) {
	--length
	for (var i = 1; i < 32; i <<= 1) {
		length = length | length >> i
	}
	return length + 1
}

export function createCanvas($id, $width, $height, $position) {
	var element = document.createElement('canvas')
	var position = $position ? $position : 'absolute'
	element.id = $id
	element.width = $width
	element.height = $height
	element.style.position = position
	element.style.opacity = 0
	transformDom(element, -500, -500, 0, 0)
	return element
}

export function transformDom($div, $x, $y, $scale, $rotate) {
	var translate = 'translate(' + $x + 'px, ' + $y + 'px) '
	var scale = 'scale(' + $scale + ') '
	var rotate = 'rotate(' + $rotate + 'deg)'
	$div.style.WebkitTransform = translate + scale + rotate
	$div.style.MozTransform = translate + scale + rotate
	$div.style.OTransform = translate + scale + rotate
	$div.style.msTransform = translate + scale + rotate
	$div.style.transform = translate + scale + rotate
}

import Zone from '../Zone'

export default class CircleZone extends Zone {
	constructor(x, y, radius) {
		super()
		this.x = x
		this.y = y
		this.radius = radius
		this.angle = 0
		this.center = {
			x: this.x,
			y: this.y,
		}
	}

	/**
	 * @override
	 */
	getPosition() {
		this.random = Math.random()
		this.angle = Math.PI * 2 * Math.random()
		this.vector.x = this.x + this.random * this.radius * Math.cos(this.angle)
		this.vector.y = this.y + this.random * this.radius * Math.sin(this.angle)
		return this.vector
	}

	setCenter(x, y) {
		this.center.x = x
		this.center.y = y
	}

	/**
	 * @override
	 */
	crossing(particle) {
		var d = particle.p.distanceTo(this.center)
		switch(this.crossType) {
		case 'dead':
			if (d - particle.radius > this.radius)
				particle.dead = true
			break
		case 'bound':
			if (d + particle.radius >= this.radius)
				this.getSymmetric(particle)
			break
		case 'cross':
			if (this.alert) {
				alert('Sorry CircleZone does not support cross method')
				this.alert = false
			}
			break
		}
	}

	getSymmetric(particle) {
		// FIXME
		var tha2 = particle.v.getGradient()
		var tha1 = this.getGradient(particle)
		var tha = 2 * (tha2 - tha1)
		var oldx = particle.v.x
		var oldy = particle.v.y
		particle.v.x = oldx * Math.cos(tha) - oldy * Math.sin(tha)
		particle.v.y = oldx * Math.sin(tha) + oldy * Math.cos(tha)
	}

	getGradient(particle) {
		return -Math.PI / 2 + Math.atan2(particle.p.y - this.center.y, particle.p.x - this.center.x)
	}
}

import Zone from '../Zone'

export default class ImageZone extends Zone {
	constructor(imageData, x, y, d) {
		super()
		this.reset(imageData, x, y, d)
	}

	reset(imageData, x = 0, y = 0, d = 2) {
		this.imageData = imageData
		this.x = x
		this.y = y
		this.d = d

		this.vectors = []
		this.setVectors()
	}

	setVectors() {
		var length1 = this.imageData.width
		var length2 = this.imageData.height
		for (var i = 0; i < length1; i += this.d) {
			for (var j = 0; j < length2; j += this.d) {
				var index = ((j >> 0) * length1 + (i >> 0)) * 4
				if (this.imageData.data[index + 3] > 0) {
					this.vectors.push({
						x: i + this.x,
						y: j + this.y,
					})
				}
			}
		}
		return this.vector
	}

	getBound(x, y) {
		var index = ((y >> 0) * this.imageData.width + (x >> 0)) * 4
		return this.imageData.data[index + 3] > 0
	}

	/**
	 * @override
	 */
	getPosition() {
		return this.vector.copy(this.vectors[Math.floor(Math.random() * this.vectors.length)])
	}

	getColor(x, y) {
		x -= this.x
		y -= this.y
		var i = ((y >> 0) * this.imageData.width + (x >> 0)) * 4
		return {
			r : this.imageData.data[i],
			g : this.imageData.data[i + 1],
			b : this.imageData.data[i + 2],
			a : this.imageData.data[i + 3]
		}
	}

	/**
	 * @override
	 */
	crossing(particle) {
		if (this.crossType == 'dead') {
			if (this.getBound(particle.p.x - this.x, particle.p.y - this.y))
				particle.dead = true
			else
				particle.dead = false
		} else if (this.crossType == 'bound') {
			if (!this.getBound(particle.p.x - this.x, particle.p.y - this.y))
				particle.v.negate()
		}
	}
}

import Zone from '../Zone'

export default class LineZone extends Zone {
	constructor(x1, y1, x2, y2, direction = '>') {
		super()
		if (x2 - x1 >= 0) {
			this.x1 = x1
			this.y1 = y1
			this.x2 = x2
			this.y2 = y2
		} else {
			this.x1 = x2
			this.y1 = y2
			this.x2 = x1
			this.y2 = y1
		}
		this.dx = this.x2 - this.x1
		this.dy = this.y2 - this.y1
		this.minx = Math.min(this.x1, this.x2)
		this.miny = Math.min(this.y1, this.y2)
		this.maxx = Math.max(this.x1, this.x2)
		this.maxy = Math.max(this.y1, this.y2)
		this.dot = this.x2 * this.y1 - this.x1 * this.y2
		this.xxyy = this.dx * this.dx + this.dy * this.dy
		this.gradient = this.getGradient()
		this.length = this.getLength()

		this.direction = direction
	}

	/**
	 * @override
	 */
	getPosition() {
		this.random = Math.random()
		this.vector.x = this.x1 + this.random * this.length * Math.cos(this.gradient)
		this.vector.y = this.y1 + this.random * this.length * Math.sin(this.gradient)
		return this.vector
	}

	getDirection(x, y) {
		var A = this.dy
		var B = -this.dx
		var C = this.dot
		var D = B == 0 ? 1 : B
		if ((A * x + B * y + C) * D > 0)
			return true
		else
			return false
	}

	getDistance(x, y) {
		var A = this.dy
		var B = -this.dx
		var C = this.dot
		var D = (A * x + B * y + C)
		return D / Math.sqrt(this.xxyy)
	}

	getSymmetric(v) {
		var tha2 = v.getGradient()
		var tha1 = this.getGradient()
		var tha = 2 * (tha1 - tha2)
		var oldx = v.x
		var oldy = v.y
		v.x = oldx * Math.cos(tha) - oldy * Math.sin(tha)
		v.y = oldx * Math.sin(tha) + oldy * Math.cos(tha)
		return v
	}

	getGradient() {
		return Math.atan2(this.dy, this.dx)
	}

	getRange(particle, fun) {
		var angle = Math.abs(this.getGradient())
		if (angle <= Math.PI / 4) {
			if (particle.p.x < this.maxx && particle.p.x > this.minx) {
				fun()
			}
		} else {
			if (particle.p.y < this.maxy && particle.p.y > this.miny) {
				fun()
			}
		}
	}

	getLength() {
		return Math.sqrt(this.dx * this.dx + this.dy * this.dy)
	}

	/**
	 * @override
	 */
	crossing(particle) {
		var self = this
		if (this.crossType == 'dead') {
			if (this.direction == '>' || this.direction == 'R' || this.direction == 'right' || this.direction == 'down') {
				this.getRange(particle, function() {
					if (self.getDirection(particle.p.x, particle.p.y))
						particle.dead = true
				})
			} else {
				this.getRange(particle, function() {
					if (!self.getDirection(particle.p.x, particle.p.y))
						particle.dead = true
				})
			}
		} else if (this.crossType == 'bound') {
			this.getRange(particle, function() {
				if (self.getDistance(particle.p.x, particle.p.y) <= particle.radius) {
					if (self.dx == 0) {
						particle.v.x *= -1
					} else if (self.dy == 0) {
						particle.v.y *= -1
					} else {
						self.getSymmetric(particle.v)
					}
				}
			})
		} else if (this.crossType == 'cross') {
			if (this.alert) {
				alert('Sorry lineZone does not support cross method')
				this.alert = false
			}
		}
	}
}

import Zone from '../Zone'

export default class PointZone extends Zone {
	constructor(x, y) {
		super()
		this.x = x
		this.y = y
	}

	/**
	 * @override
	 */
	getPosition() {
		this.vector.x = this.x
		this.vector.y = this.y
		return this.vector
	}

	/**
	 * @override
	 */
	crossing(particle) {
		if (this.alert) {
			alert('Sorry PointZone does not support crossing method')
			this.alert = false
		}
	}
}

import Zone from '../Zone'

export default class RectZone extends Zone {
	constructor(x, y, width, height) {
		super()
		this.x = x
		this.y = y
		this.width = width
		this.height = height
	}

	/**
	 * @override
	 */
	getPosition() {
		this.vector.x = this.x + Math.random() * this.width
		this.vector.y = this.y + Math.random() * this.height
		return this.vector
	}

	/**
	 * @override
	 */
	crossing(particle) {
		var p = particle.p
		var v = particle.v
		var radius = particle.radius

		switch(this.crossType) {
		case 'dead':
			if (p.x + radius < this.x) {
				particle.dead = true
			} else if (p.x - radius > this.x + this.width) {
				particle.dead = true
			}

			if (p.y + radius < this.y) {
				particle.dead = true
			} else if (p.y - radius > this.y + this.height) {
				particle.dead = true
			}
			break
		case 'bound':
			if (p.x - radius < this.x) {
				p.x = this.x + radius
				v.x *= -1
			} else if (p.x + radius > this.x + this.width) {
				p.x = this.x + this.width - radius
				v.x *= -1
			}

			if (p.y - radius < this.y) {
				p.y = this.y + radius
				v.y *= -1
			} else if (p.y + radius > this.y + this.height) {
				p.y = this.y + this.height - radius
				v.y *= -1
			}
			break
		case 'cross':
			if (p.x + radius < this.x && v.x <= 0) {
				p.x = this.x + this.width + radius
			} else if (p.x - radius > this.x + this.width && v.x >= 0) {
				p.x = this.x - radius
			}

			if (p.y + radius < this.y && v.y <= 0) {
				p.y = this.y + this.height + radius
			} else if (p.y - radius > this.y + this.height && v.y >= 0) {
				p.y = this.y - radius
			}
			break
		}
	}
}
